import React, { useEffect, useState, useRef } from 'react';
import './HomePage.css';
import { API_BASE } from '../config';

function MovieCard({ movie, commentCounts, onClick }) {
  const poster = movie.poster_url || movie.poster_path || movie.poster;
  return (
    <div className="movie-card" onClick={() => onClick && onClick(movie)}>
      <div className="movie-thumb">
        {poster ? (
          <img src={poster} alt={movie.title} loading="lazy" />
        ) : (
          <div className="no-poster">Kh√¥ng c√≥ poster</div>
        )}
        <div className="movie-overlay">
          <div className="overlay-content">
            <h3 className="movie-title">{movie.title}</h3>
            <div className="movie-meta">
              <span className="year">{movie.year}</span>
              <span className="rating">‚≠ê {movie.vote_average || 'N/A'}</span>
              <span className="comments">üí¨ {commentCounts && (commentCounts[movie.id] || 0)}</span>
            </div>
            <button className="btn-play">‚ñ∂ Xem phim</button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default function HomePage({ onMovieClick }) {
  const [allMovies, setAllMovies] = useState([]);
  const [commentCounts, setCommentCounts] = useState({});
  const [featuredMovies, setFeaturedMovies] = useState([]);
  const [newMovies, setNewMovies] = useState([]);
  const [topRatedMovies, setTopRatedMovies] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [isSearching, setIsSearching] = useState(false);
  const searchTimeoutRef = useRef(null);

  const handleSearch = async (query) => {
    setSearchQuery(query);
    if (searchTimeoutRef.current) {
      clearTimeout(searchTimeoutRef.current);
    }
    if (query.trim().length > 0) {
      searchTimeoutRef.current = setTimeout(async () => {
        setIsSearching(true);
        try {
          const response = await fetch(`${API_BASE}/movies/autocomplete?q=${encodeURIComponent(query)}&n=12`);
          if (response.ok) {
            const data = await response.json();
            setSearchResults(data.results || []);
          } else {
            setSearchResults([]);
          }
        } catch (error) {
          console.error('Search failed:', error);
          setSearchResults([]);
        }
      }, 200); // shorter debounce for snappier autocomplete
    } else {
      setIsSearching(false);
      setSearchResults([]);
    }
  };

  const fetchCommentCounts = async (movies) => {
    try {
      const ids = movies.map(m => m.id).filter(Boolean);
      if (!ids.length) return;
      // Best-effort call; backend may accept POST with movie_ids
      const resp = await fetch(`${API_BASE}/movies/comments/counts`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ movie_ids: ids }),
      });
      if (resp.ok) {
        const d = await resp.json();
        // Expecting { counts: { id: count, ... } } or a simple object
        const counts = d.counts || d || {};
        setCommentCounts(prev => ({ ...prev, ...counts }));
      }
    } catch (e) {
      // ignore; comment counts are optional
    }
  };

  useEffect(() => {
    const fetchMovies = async () => {
      // Try cached recommendations first
      try {
        const storedUser = localStorage.getItem('user');
        const user = storedUser ? JSON.parse(storedUser) : null;
        const cacheKey = `cached_recs_collaborative_${user && user.userId ? user.userId : 'public'}`;
        const raw = localStorage.getItem(cacheKey);
        if (raw) {
          const parsed = JSON.parse(raw);
          const movies = parsed.results || [];
          if (movies.length > 0) {
            const featured = movies.filter(m => m.vote_average >= 7).slice(0, 6);
            setFeaturedMovies(featured);
            setLoading(false);
            setTimeout(() => {
              setAllMovies(movies);
              setNewMovies(movies.filter(m => m.year >= 2020).slice(0, 12));
              setTopRatedMovies([...movies].sort((a, b) => (b.vote_average || 0) - (a.vote_average || 0)).slice(0, 12));
              try { fetchCommentCounts(movies); } catch (e) {}
            }, 50);
          }
        }
      } catch (e) {}

      try {
        const response = await fetch(`${API_BASE}/recommendations?rec_type=collaborative&n=18`);
        if (!response.ok) {
          setLoading(false);
          return;
        }
        const data = await response.json();
        const movies = data.results || [];
        const featured = movies.filter(m => m.vote_average >= 7).slice(0, 6);
        setFeaturedMovies(featured);
        setAllMovies(movies);
        setNewMovies(movies.filter(m => m.year >= 2020).slice(0, 12));
        setTopRatedMovies([...movies].sort((a, b) => (b.vote_average || 0) - (a.vote_average || 0)).slice(0, 12));
        try { fetchCommentCounts(movies); } catch (e) {}
        setLoading(false);

        // update cache
        try {
          const storedUser = localStorage.getItem('user');
          const user = storedUser ? JSON.parse(storedUser) : null;
          const cacheKey = `cached_recs_collaborative_${user && user.userId ? user.userId : 'public'}`;
          localStorage.setItem(cacheKey, JSON.stringify({ ts: Date.now(), results: movies }));
        } catch (e) {}
      } catch (e) {
        console.error('Failed to fetch movies:', e);
        setLoading(false);
      }
    };

    fetchMovies();
  }, []);

  if (loading) {
    return (
      <div className="home-page">
        {/* Hero Section (same as normal view) */}
        <section className="hero-section">
          <div className="hero-overlay">
            <h1 className="hero-title">üé¨ Xem Phim Online Mi·ªÖn Ph√≠</h1>
            <p className="hero-subtitle">Ch·∫•t l∆∞·ª£ng HD, c·∫≠p nh·∫≠t li√™n t·ª•c - H√†ng ngh√¨n b·ªô phim hay ƒëang ch·ªù b·∫°n</p>

            <div className="search-container">
              <input
                type="text"
                className="search-input"
                placeholder="T√¨m ki·∫øm phim..."
                value={searchQuery}
                onChange={(e) => handleSearch(e.target.value)}
              />
              <button className="search-btn">üîç</button>
            </div>
          </div>
        </section>

        {/* Skeleton for recommendations to improve perceived performance */}
        <section className="movie-section">
          <div className="section-header">
            <h2 className="section-title">G·ª£i √Ω cho b·∫°n</h2>
          </div>
          <div className="movie-grid skeleton">
            {Array.from({ length: 8 }).map((_, i) => (
              <div className="movie-card skeleton-card" key={i}>
                <div className="movie-thumb skeleton-thumb" />
                <div className="movie-overlay">
                  <div className="overlay-content">
                    <div className="movie-details-skeleton">
                      <div className="skeleton-line short" />
                      <div className="skeleton-line long" />
                    </div>
                    <button className="btn-play skeleton-btn"> </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </section>
      </div>
    );
  }

  return (
    <div className="home-page">
      {/* Hero Section */}
      <section className="hero-section">
        <div className="hero-overlay">
          <h1 className="hero-title">üé¨ Xem Phim Online Mi·ªÖn Ph√≠</h1>
          <p className="hero-subtitle">Ch·∫•t l∆∞·ª£ng HD, c·∫≠p nh·∫≠t li√™n t·ª•c - H√†ng ngh√¨n b·ªô phim hay ƒëang ch·ªù b·∫°n</p>

          {/* Search Bar */}
          <div className="search-container">
            <input
              type="text"
              className="search-input"
              placeholder="T√¨m ki·∫øm phim..."
              value={searchQuery}
              onChange={(e) => handleSearch(e.target.value)}
            />
            <button className="search-btn">üîç</button>
          </div>
        </div>
      </section>

      {/* Search Results */}
      {isSearching && (
        <section className="movie-section">
          <div className="section-header">
            <h2 className="section-title">
              üîé K·∫øt qu·∫£ t√¨m ki·∫øm "{searchQuery}" ({searchResults.length} phim)
            </h2>
          </div>
          {searchResults.length > 0 ? (
            <div className="movie-grid">
              {searchResults.map((movie, idx) => <MovieCard key={idx} movie={movie} commentCounts={commentCounts} onClick={onMovieClick} />)}
            </div>
          ) : (
            <div className="no-results">
              <p>Kh√¥ng t√¨m th·∫•y phim n√†o v·ªõi t·ª´ kh√≥a "{searchQuery}"</p>
            </div>
          )}
        </section>
      )}

      {/* Featured Movies */}
      {!isSearching && featuredMovies.length > 0 && (
        <section className="movie-section">
          <div className="section-header">
            <h2 className="section-title">üî• Phim N·ªïi B·∫≠t</h2>
          </div>
          <div className="movie-grid featured">
            {featuredMovies.map((movie, idx) => <MovieCard key={idx} movie={movie} commentCounts={commentCounts} onClick={onMovieClick} />)}
          </div>
        </section>
      )}

      {/* New Movies */}
      {!isSearching && newMovies.length > 0 && (
        <section className="movie-section">
          <div className="section-header">
            <h2 className="section-title">üÜï Phim M·ªõi C·∫≠p Nh·∫≠t</h2>
          </div>
          <div className="movie-grid">
            {newMovies.map((movie, idx) => <MovieCard key={idx} movie={movie} commentCounts={commentCounts} onClick={onMovieClick} />)}
          </div>
        </section>
      )}

      {/* Top Rated */}
      {!isSearching && topRatedMovies.length > 0 && (
        <section className="movie-section">
          <div className="section-header">
            <h2 className="section-title">‚≠ê Phim ƒê∆∞·ª£c ƒê√°nh Gi√° Cao</h2>
          </div>
          <div className="movie-grid">
            {topRatedMovies.map((movie, idx) => <MovieCard key={idx} movie={movie} commentCounts={commentCounts} onClick={onMovieClick} />)}
          </div>
        </section>
      )}
    </div>
  );
}
