          <h1>üé¨ H·ªá th·ªëng G·ª£i √Ω Phim</h1>
        </div>
        <nav className="sidebar-nav">
          <button 
            className={`nav-link ${currentPage === 'home' ? 'active' : ''}`}
            onClick={() => setCurrentPage('home')}
          >
            üè† Trang ch·ªß
          </button>
          <button 
            className={`nav-link ${currentPage === 'movies' ? 'active' : ''}`}
            onClick={() => setCurrentPage('movies')}
          >
            üé• Phim
          </button>
          <button 
            className={`nav-link ${currentPage === 'recommendations' ? 'active' : ''}`}
            onClick={() => setCurrentPage('recommendations')}
          >
            ‚≠ê G·ª£i √Ω
          </button>
        </nav>
      </aside>

      <main className="main-content">
        {currentPage === 'home' && <HomePage />}
        {currentPage === 'movies' && <MoviesPage />}
        {currentPage === 'recommendations' && <RecommendationsPage />}
      </main>
    </div>
  );
}
