import React, { useState, useEffect } from 'react';
import { API_BASE } from '../config';

export default function MoviesPage() {
  const [searchQuery, setSearchQuery] = useState('');
  const [movies, setMovies] = useState([]);
  const [suggestions, setSuggestions] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [showSuggestions, setShowSuggestions] = useState(false);
  const [selectedMovieId, setSelectedMovieId] = useState(null); // Movie ƒëang xem trailer

  useEffect(() => {
    if (searchQuery.trim().length > 0) {
      setLoading(true);
      setError('');
      fetch(`${API_BASE}/movies/search?q=${encodeURIComponent(searchQuery)}`)
        .then(res => res.json())
        .then(data => {
          setSuggestions(data.results || []);
          setShowSuggestions(true);
          setLoading(false);
        })
        .catch(err => {
          setError('L·ªói t√¨m ki·∫øm g·ª£i √Ω');
          setLoading(false);
        });
    } else {
      setSuggestions([]);
      setShowSuggestions(false);
    }
  }, [searchQuery]);

  const handleSelectMovie = async (movie) => {
    setSearchQuery(movie.title);
    setMovies([movie]);
    setShowSuggestions(false);
    setSelectedMovieId(null);

    // Fetch trailer ngay khi ch·ªçn
    try {
      const response = await fetch(`${API_BASE}/movies/${movie.id}/trailer`);
      if (response.ok) {
        const data = await response.json();
        setMovies([{ ...movie, video_url: data.video_url }]);
      }
    } catch (err) {
      console.error('Error fetching trailer:', err);
    }
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!searchQuery.trim()) return;
    setLoading(true);
    setError('');

    try {
      const response = await fetch(`${API_BASE}/movies/search?q=${encodeURIComponent(searchQuery)}`);
      if (!response.ok) throw new Error('L·ªói t√¨m ki·∫øm');
      const data = await response.json();
      setMovies(data.results || []);
      setShowSuggestions(false);
      if (!data.results || data.results.length === 0) setError('Kh√¥ng t√¨m th·∫•y phim n√†o');
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const getYouTubeVideoId = (url) => {
    if (!url) return null;
    try {
      const urlObj = new URL(url);
      if (urlObj.hostname.includes('youtube.com')) return urlObj.searchParams.get('v');
      if (urlObj.hostname.includes('youtu.be')) return urlObj.pathname.slice(1);
    } catch (e) {
      return null;
    }
    return null;
  };

  const toggleTrailer = async (movie) => {
    if (selectedMovieId === movie.id) {
      setSelectedMovieId(null); // ·∫®n trailer n·∫øu click l·∫°i
      return;
    }

    // N·∫øu ch∆∞a c√≥ video_url, fetch t·ª´ backend
    if (!movie.video_url) {
      try {
        const response = await fetch(`${API_BASE}/movies/${movie.id}/trailer`);
        if (response.ok) {
          const data = await response.json();
          setMovies(mvs => mvs.map(m => m.id === movie.id ? { ...m, video_url: data.video_url } : m));
        }
      } catch (err) {
        console.error('Error fetching trailer:', err);
      }
    }

    setSelectedMovieId(movie.id); // Hi·ªÉn th·ªã trailer cho movie n√†y
  };

  const MovieCard = ({ movie }) => {
    const videoId = getYouTubeVideoId(movie.video_url);

    return (
      <div 
        key={movie.id || movie.title} 
        className="movie-card"
        style={{ cursor: 'pointer', transition: 'all 0.3s ease', padding: '15px', border: '1px solid #e5e7eb', borderRadius: '8px', marginBottom: '15px' }}
        onClick={() => toggleTrailer(movie)}
      >
        <h3>{movie.title} {movie.year > 0 && `(${movie.year})`}</h3>
        <p>{movie.overview}</p>
        <p>‚≠ê {movie.vote_average || 'N/A'}/10</p>
        <p style={{ color: '#3b82f6', fontSize: '14px', fontWeight: '500' }}>‚ñ∂ Click ƒë·ªÉ xem trailer</p>

        {/* Hi·ªÉn th·ªã trailer ngay d∆∞·ªõi phim n·∫øu movie ƒëang ƒë∆∞·ª£c ch·ªçn */}
        {selectedMovieId === movie.id && movie.video_url && videoId && (
          <iframe 
            width="100%" 
            height="400" 
            src={`https://www.youtube.com/embed/${videoId}?autoplay=1`} 
            frameBorder="0" 
            allow="autoplay; encrypted-media" 
            allowFullScreen 
            title="trailer"
            style={{ marginTop: '15px', borderRadius: '8px' }}
          />
        )}
      </div>
    );
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>üé• T√¨m ki·∫øm Phim</h1>
      <form onSubmit={handleSearch} style={{ position: 'relative', marginBottom: '20px' }}>
        <input 
          type="text" 
          placeholder="Nh·∫≠p t√™n phim..." 
          value={searchQuery} 
          onChange={(e) => setSearchQuery(e.target.value)} 
          style={{ padding: '10px', width: '300px', marginRight: '10px' }}
        />
        <button type="submit" style={{ padding: '10px' }}>T√¨m ki·∫øm</button>

        {showSuggestions && suggestions.length > 0 && (
          <div style={{ position: 'absolute', top: '45px', left: 0, right: 0, maxWidth: '300px', background: 'white', border: '1px solid #d1d5db', borderRadius: '6px', maxHeight: '200px', overflowY: 'auto', zIndex: 10 }}>
            {suggestions.slice(0, 10).map((movie, idx) => (
              <div key={idx} onClick={() => handleSelectMovie(movie)} style={{ padding: '10px', borderBottom: '1px solid #e5e7eb', cursor: 'pointer' }}>
                {movie.title} {movie.year > 0 && `(${movie.year})`}
              </div>
            ))}
          </div>
        )}
      </form>

      {error && <div style={{ color: 'red' }}>{error}</div>}
      {loading && <div>ƒêang t·∫£i...</div>}

      {movies.length > 0 && (
        <div>
          <h3>T√¨m th·∫•y {movies.length} k·∫øt qu·∫£</h3>
          <div>
            {movies.map(movie => <MovieCard key={movie.id || movie.title} movie={movie} />)}
          </div>
        </div>
      )}
    </div>
  );
}
